    private Property exhaustMinValue = new SimpleStringProperty("500");
    private Property exhaustMaxValue = new SimpleStringProperty("650");
    private Property irrigationMinValue = new SimpleStringProperty("3");
    private Property irrigationMaxValue = new SimpleStringProperty("40");

