    public static boolean isSensorFrame(byte[] iBytes) {
        int len = iBytes.length;
        if ((0xEE == (iBytes[0] & 0xff)) && len == 14 && (0xFF == (iBytes[len - 1] & 0xff))) {
            return true;
        } else {
            return false;
        }
    }

